	iwpriv wlan0 set_mib pwrlevelHT40_1S_B=5f5f5f5e5d5c5b5b5a5a59595959
	iwpriv wlan0 set_mib pwrdiffHT40_2S=fdfdfdfdfdfdfdfdfdfdfdfdfdfd
	iwpriv wlan0 set_mib pwrdiffHT20=00ffffff00011000110010000000
