#!/bin/sh

anlix_bootup_defaults() {

	# Get all parameters needed
	# interface 1: 5G
	# interface 2: 2.4G
	local _parameters="$(anlix-flash-utils \
2 x_cap \
2 thermal \
2 power_level_cck_a \
2 power_level_cck_b \
2 power_level_ht40_1s_a \
2 power_level_ht40_1s_b \
2 power_diff_ht40_2s \
2 power_diff_ht20 \
2 power_diff_ofdm \
2 tx_power_tssi_ht40_1s_a \
2 tx_power_tssi_ht40_1s_b \
1 x_cap \
1 thermal \
1 tx_power_5g_ht40_1s_a \
1 tx_power_5g_ht40_1s_b \
1 tx_power_diff_5g_ofdm \
1 tx_power_diff_5g_80bw1s_160bw1s_a \
1 tx_power_diff_5g_80bw1s_160bw1s_b \
1 tx_power_tssi_5g_ht40_1s_a \
1 tx_power_tssi_5g_ht40_1s_b)"

	if [ ! -z "$_parameters" ] && [ -n "$(echo $_parameters | grep '[ ERROR ]')" ]
	then

		get_data 20 _parameter $_parameters

		ifconfig wlan0 up
		iwpriv wlan0 set_mib xcap=$_parameter0
		iwpriv wlan0 set_mib ther=$_parameter1
		iwpriv wlan0 set_mib pwrlevelCCK_A=$_parameter2
		iwpriv wlan0 set_mib pwrlevelCCK_B=$_parameter3
		iwpriv wlan0 set_mib pwrlevelHT40_1S_A=$_parameter4
		iwpriv wlan0 set_mib pwrlevelHT40_1S_B=$_parameter5
		iwpriv wlan0 set_mib pwrdiffHT40_2S=$_parameter6
		iwpriv wlan0 set_mib pwrdiffHT20=$_parameter7
		iwpriv wlan0 set_mib pwrdiffOFDM=$_parameter8
		iwpriv wlan0 set_mib pwrlevel_TSSIHT40_1S_A=$_parameter9
		iwpriv wlan0 set_mib pwrlevel_TSSIHT40_1S_B=$_parameter10
		ifconfig wlan0 down

		ifconfig wlan1 up
		iwpriv wlan0 set_mib txbf=1
		iwpriv wlan1 set_mib xcap=$_parameter11
		iwpriv wlan1 set_mib ther=$_parameter12
		iwpriv wlan1 set_mib pwrlevel5GHT40_1S_A=$_parameter13
		iwpriv wlan1 set_mib pwrlevel5GHT40_1S_B=$_parameter14
		iwpriv wlan1 set_mib pwrdiff5GOFDM=$_parameter15
		iwpriv wlan1 set_mib pwrdiff_5G_80BW1S_160BW1S_A=$_parameter16
		iwpriv wlan1 set_mib pwrdiff_5G_80BW1S_160BW1S_B=$_parameter17
		iwpriv wlan1 set_mib pwrlevel_TSSI5GHT40_1S_A=$_parameter18
		iwpriv wlan1 set_mib pwrlevel_TSSI5GHT40_1S_B=$_parameter19
		ifconfig wlan1 down

	else

		ifconfig wlan0 up
		iwpriv wlan0 set_mib xcap=0
		iwpriv wlan0 set_mib ther=27
		iwpriv wlan0 set_mib pwrlevelCCK_A=5c5c5c5c5c5c5c5c5c5c5c5c5c5c
		iwpriv wlan0 set_mib pwrlevelCCK_B=5353535353535353535353535353
		iwpriv wlan0 set_mib pwrlevelHT40_1S_A=6464646363636363635d5d5d5d5d
		iwpriv wlan0 set_mib pwrlevelHT40_1S_B=5c5c5c5e5e5e5e5e5e5a5a5a5a5a
		iwpriv wlan0 set_mib pwrdiffHT40_2S=0000000000000000000000000000
		iwpriv wlan0 set_mib pwrdiffHT20=adadadadadadadadadadadadadad
		iwpriv wlan0 set_mib pwrdiffOFDM=c9c9c9c9c9c9c9c9c9c9c9c9c9c9
		iwpriv wlan0 set_mib pwrlevel_TSSIHT40_1S_A=0e0e0e1111111111111a1a1a1a1a
		iwpriv wlan0 set_mib pwrlevel_TSSIHT40_1S_B=1919190f0f0f0f0f0f0c0c0c0c0c
		ifconfig wlan0 down

		ifconfig wlan1 up
		iwpriv wlan0 set_mib txbf=1
		iwpriv wlan1 set_mib xcap=65
		iwpriv wlan1 set_mib ther=33
		iwpriv wlan1 set_mib pwrlevel5GHT40_1S_
		iwpriv wlan1 set_mib pwrlevel5GHT40_1S_
		iwpriv wlan1 set_mib 
		iwpriv wlan1 set_mib pwrdiff_5G_80BW1S_160BW1S_
		iwpriv wlan1 set_mib pwrdiff_5G_80BW1S_160BW1S_
		iwpriv wlan1 set_mib pwrlevel_TSSI5GHT40_1S_
		iwpriv wlan1 set_mib pwrlevel_TSSI5GHT40_1S_
		ifconfig wlan1 down

	fi
}

get_custom_mac() {
	. /lib/functions/system.sh
	local _mac_address_tag=""
	local _p1

	_p1=$(mtd_get_mac_binary config 7 | awk '{print toupper($1)}')
	[ ! -z "$_p1" ] && _mac_address_tag=$_p1

	echo "$_mac_address_tag"
}

custom_switch_ports() {
	case $1 in 
		1) echo "switch0" ;;
		2) echo "0" ;;
		3) echo "1 2 3" ;;
		4) echo "6" ;;
		5) echo "3" ;;
	esac
}

